mapper.select:
select c.id as cardId, c.cardName as cardName, c.defaultCard as cardDefaultCard, c.unit_id as unit_id, c.picUrl as picUrl, u.pointId as pointId, u.pointName as pointName, u.pointRate as pointRate, fi.id as pic_id, fi.description as pic_description, fi.filesize as pic_filesize, fi.mimeType as pic_mimeType, fi.originalFilename as pic_originalFilename, fi.content as pic_content, qy.nid as qy_id, qy.qymc as qy_name, qy.qybh as qy_code , cc.id as ccid , cc.card_Id as card_Id , cc.company_Id as company_Id from card c left join companyCard cc on c.id = cc.card_Id left join fileitem fi on c.picUrl = fi.id left join unit u on c.unit_id = u.pointId left join tbl_qy qy on cc.company_Id = qy.nid
c.cardName like '%${cardName}%'
and c.defaultCard = #{defaultCard}
order by c.id desc limit #{startIndex}, #{pageSize}

select count(c.id) from card c left join companyCard cc on c.id = cc.card_Id left join fileitem fi on c.picUrl = fi.id left join unit u on c.unit_id = u.pointId left join tbl_qy qy on cc.company_Id = qy.nid
c.cardName like '%${cardName}%'
and c.defaultCard = #{defaultCard}
